# get a random number from 0 to the number passed into the function
def getRandom(num):
    return randint(0,num)

@app.route("/", methods=["GET", "POST"])
def main():
    # randomly choose rick or stick
    choice = getRandom(2)
    if choice == 1:
        return render_template('display.html', image=ricks[getRandom(5)])
    else:
        return render_template('display.html', image=ricks[getRandom(5)])

app.run(debug=True)
